import {useState, useEffect} from 'react';
import "./ShoeDetail.css"


function ShoeDetail() {
    const [shoe, setShoe] = useState([]);

    async function loadShoe() {
        const response = await fetch('http://localhost:8080/api/shoe/23/');
        const data = await response.json();
        setShoe(data);
        console.log(data.picture_url)
    }
    useEffect(() => {
        loadShoe();
    }, []);



    // async function handleOnDelete(e) {
    //     const pk = e.target.id
    //     const request = await fetch (`http://localhost:8080/api/shoe/${pk}/`, {
    //         method: "DELETE"
    //     });
    //     const resp = await request.json()
    //     if (resp.deleted){
    //         loadShoe()
    //     } else {
    //         alert("Not Deleted!")
    //     }
    // }

return (
    <div className="container1">
        <div className="card cardContainer" key={shoe.id}>
                <div className="nameContainer">
                    <h1 className="name">{shoe.name}</h1>
                </div>
                <div className="col gap-3 nameContainer">
                <p className='gender'>{shoe.gender}'s</p>
                </div>
                <div>
                <img src={shoe.picture_url} className="imgCard card-img-top"></img>
                </div>
                <table className="table mainTable">
                <thead className="tableHead">
                    <tr className="headTR">
                        <th className='headData'>Brand</th>
                        <th className='headData'>Category</th>
                        <th className='headData'>Price</th>
                        <th className='headData'>Size</th>
                        <th className='headData'>Color</th>
                        <th className='headData'>Sku</th>
                        {/* add the description */}
                    </tr>
                </thead>
                <tbody className='tableBody'>
                    <tr className="bodyTR">
                        <td className='tableData'>{shoe.brand}</td>
                        <td className='tableData'>{shoe.category}</td>
                        <td className='tableData'>${shoe.price}</td>
                        <td className='tableData'>{shoe.size}</td>
                        <td className='tableData'>{shoe.color}</td>
                        <td className='tableData'>{shoe.sku}</td>
                    </tr>
                </tbody>
                </table>
        </div>
    </div>
);
}
export default ShoeDetail;
