import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar, faX } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import "./ShoePage.css"
function StarRating() {
    const [hoveredStar, setHoveredStar] = useState(null);
    const [showModel, setShowModel] = useState(false);
    const [selectedStar, setSelectedStar] = useState(null);

    const [csrfToken, setCsrfToken] = useState('');
    const [formData, setFormData] = useState({
        rating: '',
        rating_description: '',
    });
    const navigate = useNavigate();


    const handleStarClick = (starNumber) => {
        setSelectedStar(starNumber);
        setShowModel(true);
        console.log(showModel)
    };


    const handleSubmit = async (event) => {
        // event.preventDefault();
        // const accountUrl = 'http://localhost:8070/api/account/login/';
        // const fetchConfig = {
        //     method: "POST",
        //     body: JSON.stringify(formData),
        //     headers: {
        //         'Content-Type': 'application/json',
        //         'X-CSRFToken': csrfToken,
        //     },
        //     credentials: 'include',
        //     }
        // try {
        //     const response = await fetch(accountUrl, fetchConfig);

        //     if (response.ok) {
        //         setFormData({
        //             rating: '',
        //             rating_description: ''
        //         });
        //         navigate("/")
        //     } else {
        //         console.error('Login failed');
        //     }
        // } catch (error) {
        //     console.error('Error during Login:', error);
        // }
    }


    // const handleFormChange = (e) => {
    //     const value = e.target.value;
    //     const inputName = e.target.name;
    //     setFormData({
    //         ...formData,
    //         [inputName]: value
    //     });
    // }

    return (
        <div className="products_star">
            {[1, 2, 3, 4, 5].map((starNumber) => (
                <FontAwesomeIcon
                    key={starNumber}
                    className="starIcon"
                    icon={faStar}
                    onMouseEnter={() => setHoveredStar(starNumber)}
                    onMouseLeave={() => setHoveredStar(null)}
                    onClick={() => handleStarClick(starNumber)}
                    style={{color: (starNumber <= (hoveredStar || selectedStar)) ? 'gold' : 'grey'}}
                />
            ))}
                {showModel && <div className="overlay"></div>}
                <div className={showModel ? "ratingModel show" : "ratingModel"}>
                    <div className='modelWindowXAndRate'>
                        <div className='rateStarsDiv'>
                        <h2 className='rateStars'>Rate {selectedStar} stars </h2>
                        </div>
                    <div className='exitButtonDiv'>
                        <button onClick={() => setShowModel(false)} className='exitButton'><FontAwesomeIcon size="xssm"icon={faX} /></button>
                    </div>
                    </div>
                    <div className='ratingDescDiv'>
                    <textarea className='ratingDesc' placeholder="Describe your rating..."></textarea>
                    </div>
                    <div className='submitRatingDiv'>
                        <button className="submitRating" onClick={() => setShowModel(false)}>Submit</button>
                    </div>
                </div>
        </div>
    );
}

export default StarRating;
