import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons';

function StarRating() {
    const [hoveredStar, setHoveredStar] = useState(4);
    const [selectedStar, setSelectedStar] = useState(null);

    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });
    const navigate = useNavigate();
    const { login } = useAuth();
    const [csrfToken, setCsrfToken] = useState('');

    useEffect(() => {
        const fetchCsrfToken = async () => {
        try {
            const response = await fetch('http://localhost:8070/api/csrf-token/', {
                method: 'GET',
                credentials: 'include',
            });
        if (response.ok) {
            const data = await response.json();
            setCsrfToken(data.csrfToken);
        } else {
            console.error('Failed to fetch CSRF token.');
        }
        } catch (error) {
            console.error('Error fetching CSRF token:', error);
        }
        };

        fetchCsrfToken();
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/account/login/';
        const fetchConfig = {
            method: "POST",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': csrfToken,
            },
            credentials: 'include',
            }
        try {
            const response = await fetch(accountUrl, fetchConfig);

            if (response.ok) {
                login();
                setFormData({
                    username: '',
                    password: '',
                });
                navigate("/")
            } else {
                console.error('Login failed');
            }
        } catch (error) {
            console.error('Error during Login:', error);
        }
    }


    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }
    const getColor = (starNumber) => {
        if (starNumber <= hoveredStar) return 'gold';
        if (starNumber <= selectedStar) return 'gold';
        return 'grey';
    };

    return (
        <div className="products_star">
            {[1, 2, 3, 4, 5].map((starNumber) => (
                <FontAwesomeIcon
                    key={starNumber}
                    className="starIcon"
                    icon={faStar}
                    onMouseEnter={() => setHoveredStar(starNumber)}
                    onMouseLeave={() => setHoveredStar(starNumber)}
                    onClick={() => setSelectedStar(starNumber)}
                    style={{color: getColor(starNumber)}}
                />
            ))}
        </div>
    );
}

export default StarRating;
