import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  faX } from '@fortawesome/free-solid-svg-icons';
import { faStar as farFaStar } from '@fortawesome/free-regular-svg-icons';
import { faStar as fasFaStar } from '@fortawesome/free-solid-svg-icons';
import { faStarHalfAlt } from '@fortawesome/free-solid-svg-icons';
import "../ShoePage.css"


function StarRating({ shoeID, isClickable = true, ratingValue = null, forceRatingValue = null }) {
    const [hoveredStar, setHoveredStar] = useState(null);
    const [shoeRating, setShoeRating] = useState(ratingValue || null);
    const [selectedStar, setSelectedStar] = useState(null);
    const previousShoeID = useRef(null);

    const [formData, setFormData] = useState({
        rating: '',
        rating_description: '',
    });


    async function loadRating() {
        try {
            const response = await fetch(`http://localhost:8080/api/shoe/${shoeID}/`);

            if (!response.ok) {
                throw new Error('Response was not ok');
            }

            const data = await response.json();
            console.log(data)
            if (data.serialized_ratings) {
                const averageRating = data.serialized_ratings.average_rating;
                const roundedRating = Math.round(averageRating * 2) / 2;
                if (averageRating !== undefined) {
                    setShoeRating(roundedRating);
                }
                if (ratingValue === "average"){
                    setShoeRating(roundedRating)
                }
            }
        } catch (error) {
            console.error('Cannot get the rating:', error);
        }
    }
    useEffect(() => {
        if (shoeID !== previousShoeID.current && ratingValue !== null) {
            loadRating();
            previousShoeID.current = shoeID;
        } else {
            setShoeRating(null);
        }
    }, [shoeID, ratingValue]);


    const handleSubmit = async (event) => {
        event.preventDefault();
        const ratingUrl = `http://localhost:8080/api/ratings/${shoeID}/`;
        const fetchConfig = {
            method: "POST",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
            },
            }
        try {
            const response = await fetch(ratingUrl, fetchConfig);

            if (response.ok) {
                setFormData({
                    rating: '',
                    rating_description: '',
                });

            } else {
                console.error('Rating failed');
            }
        } catch (error) {
            console.error('Error during rating:', error);
        }
    }


    const handleStarClick = (e, starNumber) => {
        e.preventDefault();

        const starElement = e.currentTarget.getBoundingClientRect();
        const starHalfWidth = starElement.width / 2;
        const clickPosition = e.clientX - starElement.left;

        if (clickPosition < starHalfWidth) {
            setSelectedStar(starNumber - 0.5);
        } else {
            setSelectedStar(starNumber);
        }
    };


    const handleStarMouseMove = (e, starNumber) => {
        const starElement = e.currentTarget.getBoundingClientRect();
        const starHalfWidth = starElement.width / 2;
        const hoverPosition = e.clientX - starElement.left;

        if (hoverPosition < starHalfWidth) {
            setHoveredStar(starNumber - 0.5);
        } else {
            setHoveredStar(starNumber);
        }
    };



    return (
    <div className="products_star">
        {[1, 2, 3, 4, 5].map((starNumber) => {
            let iconToUse = farFaStar;
            const currentRating = forceRatingValue || hoveredStar || shoeRating;

            if (currentRating >= starNumber) {
                iconToUse = fasFaStar;
            } else if (currentRating >= starNumber - 0.5) {
                iconToUse = faStarHalfAlt;
            }
            return (
                <FontAwesomeIcon
                    key={starNumber}
                    className="starIcon fa-fw"
                    icon={iconToUse}
                    onMouseMove={isClickable ? (e) => handleStarMouseMove(e, starNumber) : null}
                    onMouseLeave={isClickable ? () => setHoveredStar(null) : null}
                    onClick={isClickable ? (e) => handleStarClick(e, starNumber) : null}
                    color="transparent"
                    style={{ color: iconToUse !== farFaStar ? 'gold' : 'grey' }}
                />
            );
        })}
        </div>
    );
}

export default React.memo(StarRating);
