import React, { useState } from 'react';
import "./AccountForm.css"
import { useNavigate } from 'react-router-dom';
function AccountLogIn() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/account/login/';
        const fetchConfig = {
            method: "POST",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
            },
            }
        try {
            const response = await fetch(accountUrl, fetchConfig);

            if (response.ok) {
                setFormData({
                    username: '',
                    password: '',
                });
                navigate("/")
            } else {
                console.error('Login failed');
            }
        } catch (error) {
            console.error('Error during Login:', error);
        }
    }


    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    return (
        <div className="row">
            <div className="offset-3 col-5 mt-4 form1">
                <div className="shadow p-4 mt-4">
                    <div className="col-12 mb-3 mt-0 pt-0 formHeaderContainer" >
                    <h1 className='text-center formHeader'>Cr With Us</h1>
                    </div>
                    <form onSubmit={handleSubmit} id="create-account-form">
                        <div className="col-12">
                            <input placeholder="Username..." className="form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                        </div>
                        <div className="col-12 mt-2">
                            <input placeholder="Password..." className="form-control" onChange={handleFormChange} value={formData.password} required type="text" name="password" id="password"/>
                        </div>
                        <button className="btn btn-primary mt-4">Create</button>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default AccountLogIn;
