import {useState} from 'react';


export function FilterBrands({ shoes, onBrandSelected }) {
    const [selectedBrand, setSelectedBrand] = useState("");

    const handleBrandChange = (e) => {
        setSelectedBrand(e.target.value);
        onBrandSelected(e.target.value);
    };

    let uniqueBrands = new Set();
    shoes.forEach(shoe => uniqueBrands.add(shoe.brand));
    let brandsArray = [...uniqueBrands];

    return (
        <div>
            <select value={selectedBrand} onChange={handleBrandChange} required name="brand" id="brand" className="form-select">
                <option className="text-center">Brand</option>
                {brandsArray.map(brand => (
                    <option className="text-center" key={brand} value={brand}>
                        {brand}
                    </option>
                ))}
            </select>
        </div>
    );
}


export function FilterCategory({ shoes, onCategorySelected }) {
    const [selectedCategory, setSelectedCategory] = useState("");

    const handleCategoryChange = (e) => {
        setSelectedCategory(e.target.value);
        onCategorySelected(e.target.value);
    };

    let uniqueCategories = new Set();
    shoes.forEach(shoe => uniqueCategories.add(shoe.category));
    let categoriesArray = [...uniqueCategories];

    return (
        <div>
            <select value={selectedCategory} onChange={handleCategoryChange} required name="category" id="category" className="form-select">
                <option className="text-center">Category</option>
                {categoriesArray.map(category => (
                    <option className="text-center" key={category} value={category}>
                        {category}
                    </option>
                ))}
            </select>
        </div>
    );
}


export function FilterPrice({ shoes, onPriceSelected }) {
    const priceRanges = [
    { label: '1-50', min: 1, max: 50 },
    { label: '51-100', min: 51, max: 100 },
    { label: '101-150', min: 101, max: 150 },
    { label: '151-200', min: 151, max: 200 }
];
    const [selectedPrice, setSelectedPrice] = useState("");

    const handlePriceChange = (e) => {
        setSelectedPrice(e.target.value);
        onPriceSelected(e.target.value);
    };

    let uniquePrices = new Set();
    shoes.forEach(shoe => uniquePrices.add(shoe.price));
    let pricesArray = [...uniquePrices];

    return (
        <div>
            <select value={selectedPrice} onChange={handlePriceChange} required name="price" id="price" className="form-select">
                <option className="text-center">Price</option>
                {pricesArray.map(price => (
                    <option className="text-center" key={price} value={price}>
                        {price}
                    </option>
                ))}
            </select>
        </div>
    );
}
