import React, { useState } from 'react';
import './ShoeForm1.css';

function AccountForm() {
    const [passwordConfirm, setPasswordConfirm] = useState("");
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        name: "",
        brand: "",
        categoryr: "",
        price: "",
        size: "",
        color: "",
        sku: "",
        bin: ""
    });

    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/accounts/';
        const fetchConfig = {
            method: "post",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
            },
            credentials: 'include',
        }

        if (formData.password === passwordConfirm) {
            const response = await fetch(accountUrl, fetchConfig);
            if (response.ok) {
                login()
                setFormData({
                    username: '',
                    first_name: '',
                    last_name: '',
                    email: '',
                    password: '',
                });
                setPasswordConfirm('');
                navigate("/");
            }
        } else {
            alert("Passwords Do Not Match");
        }
    }

    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    const nextStep = () => setCurrentStep(currentStep + 1);
    const prevStep = () => setCurrentStep(currentStep - 1);

    return (
        <div className="signup-container">
            <h1>Join FLX Rewards</h1>
            <Stepper currentStep={currentStep} />

            <form onSubmit={handleSubmit} id="create-service-form">
                {
                    (() => {
                        switch (currentStep) {
                            case 1:
                                return (
                                    <>
                                        <h2>Account Details</h2>
                                        <div className="col-12 mb-2">
                                            <input placeholder="Name..." className="text-center form-control" onChange={handleFormChange} value={formData.name} required type="text" name="name" id="name"/>
                                        </div>
                                        {/* ... other input fields */}
                                    </>
                                );
                            // ... other cases
                        }
                    })()
                }
                <div className="navigation-buttons">
                    {currentStep > 1 && <button onClick={prevStep}>Back</button>}
                    {currentStep < 3 && <button onClick={nextStep}>Next</button>}
                    {currentStep === 3 && <button type="submit">Create</button>}
                </div>
            </form>
        </div>
    );
};

const Stepper = ({ currentStep }) => {
    return (
        <div className="stepper">
            {/* ... stepper dots and labels */}
        </div>
    );
};

export default AccountForm;
