import React, { useState } from 'react';
import "./AccountForm.css"
function AccountForm() {
    const [passwordConfirm, setPasswordConfirm] = useState("")
    const [formData, setFormData] = useState({
        username: '',
        first_name: '',
        last_name: '',
        email: '',
        password: '',
    });

    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/accounts/';
        const fetchConfig = {
            method: "post",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
            },
            }

        if (formData.password === passwordConfirm){
            const response = await fetch(accountUrl, fetchConfig);
                if (response.ok) {
                    setFormData({
                        username: '',
                        first_name: '',
                        last_name: '',
                        email: '',
                        password: '',
                    });
                    setPasswordConfirm('')
                }
        } else {
            alert("Passwords Do Not Match")
        }

    }

    const handlePasswordConfirmChange = (e) => {
        setPasswordConfirm(e.target.value);
    }

    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    return (
        <div className="row">
            <div className="offset-3 col-6 mt-4 form1">
                <div className="shadow p-4 mt-4">
                    <div className="col-12 mb-3 mt-0 pt-0 formHeaderContainer" >
                    <h1 className='text-center formHeader'>Create an Account With Us</h1>
                    </div>
                    <form onSubmit={handleSubmit} id="create-account-form">
                        <div className="row">
                        <div className="col-12">
                            <input placeholder="Username..." className="form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                        </div>
                        <div className="col-6 mt-3 inputFields">
                            <input placeholder="First Name..." className="form-control" onChange={handleFormChange} value={formData.first_name} required type="text" name="first_name" id="first_name"/>
                        </div>
                        <div className="col-6 mt-3 inputFields">
                            <input placeholder="Last Name..." className="form-control" onChange={handleFormChange} value={formData.last_name} required type="text" name="last_name" id="last_name"/>
                        </div>
                        <div className="col-12 mt-3">
                            <input placeholder="Email..." className="form-control" onChange={handleFormChange} value={formData.email} required type="text" name="email" id="email"/>
                        </div>
                        <div className="col-12 mt-3">
                            <input placeholder="Password..." className="form-control" onChange={handleFormChange} value={formData.password} required type="text" name="password" id="password"/>
                        </div>
                        <div className="col-12 mt-3">
                            <input placeholder="Password Confirmation..." className="form-control" onChange={handlePasswordConfirmChange} value={passwordConfirm} required type="text" name="passwordConfirm" id="passwordConfirm"/>
                        </div>
                        <button className="btn btn-primary mt-4">Create</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default AccountForm;
