import {useState, useEffect} from 'react';

function ShoeList() {
    const [shoes, setShoes] = useState([]);
    const [bins, setBins] = useState([]);
    const [bin, setBin] = useState("");
    const [allShoes, setAllShoes] = useState("");
    const [sortDirection, setSortDirection] = useState("up");

    async function loadShoes() {
    const response = await fetch('http://localhost:8080/api/shoes/');
    const data = await response.json();
    setShoes(data.shoes);
    }

    async function loadBins(){
        const response = await fetch ("http://localhost:8100/api/bins/");
        const data = await response.json();
        setBins(data.bins)
    }

    useEffect(() => {
        loadShoes();
        loadBins();
    }, []);

    async function filterShoes() {
    const pk = bin
    const response = await fetch(`http://localhost:8080/api/bin/${pk}/shoes/`);
    const data = await response.json();
    setShoes(data.shoes);
    }

    function handleBinChange(e){
            setBin(e.target.value)
        }
    useEffect(() => {
    filterShoes();
    }, [bin]);

    function handleAllShoesChange(e){
            setAllShoes(e.target.value)
            loadShoes();
        }
    useEffect(() => {
    loadShoes();
    }, [allShoes]);


    async function handleOnDelete(e) {
        const pk = e.target.id
        const request = await fetch (`http://localhost:8080/api/shoe/${pk}/`, {
            method: "DELETE"
        });
        const resp = await request.json()
        if (resp.deleted){
            loadShoes()
        } else {
            alert("Not Deleted!")
        }
    }


    function handleSortChange(e){
        setSortDirection(e.target.value)
    }

    const sortedList = shoes.sort((conferenceA, conferenceB) => {
        if (sortDirection === "up"){
            return (conferenceA.id - conferenceB.id)
        } else {
            return (conferenceB.id - conferenceA.id)
        }
    })

return (
    <>
    <select onChange={handleSortChange}>
        <option value="up">Up</option>
        <option value="down">Down</option>
    </select>
    <select value={bin} onChange={handleBinChange} required name="bin" id="bin">
        <option>Choose a bin</option>
        {bins.map(bin => {
            return (
            <option key={bin.id} value={bin.id} >
            {bin.closet_name}
            </option>
            );
        })}
    </select>
    <button value={allShoes} onChange={handleAllShoesChange}/>
<table className="table table-striped">
    <thead>
    <tr>
        <th>IDs</th>
        <th>Names</th>
        <th>Brands</th>
        <th>Categories</th>
        <th>Prices</th>
        <th>Colors</th>
        <th>Bins</th>
        <th>Remove</th>
    </tr>
    </thead>
    <tbody>
    {sortedList.map(shoe => {
        return (
        <tr key={shoe.id}>
            <td>{ shoe.id }</td>
            <td>{ shoe.name }</td>
            <td>{ shoe.brand }</td>
            <td>{ shoe.category }</td>
            <td>{ shoe.price }</td>
            <td>{ shoe.color }</td>
            <td>{ shoe.bin.id }</td>
            <td><button onClick={handleOnDelete} id={shoe.id}className="btn btn-danger">Delete</button></td>
        </tr>
        );
    })}
    </tbody>
</table>
</>
);
}
export default ShoeList;
