import React, { useState } from 'react';
import './ShoeForm1.css';  // Assuming you'll add some CSS


    function ShoeForm1() {
        const [passwordConfirm, setPasswordConfirm] = useState("")
        const [currentStep, setCurrentStep] = useState(1);
        const [formData, setFormData] = useState({
                name: "",
                brand: "",
                categoryr: "",
                price: "",
                size: "",
                color: "",
                sku: "",
                bin: ""
        });


    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/accounts/';
        const fetchConfig = {
            method: "post",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': csrfToken,
            },
            credentials: 'include',
            }

        if (formData.password === passwordConfirm){
            const response = await fetch(accountUrl, fetchConfig);
                if (response.ok) {
                    login()
                    setFormData({
                        username: '',
                        first_name: '',
                        last_name: '',
                        email: '',
                        password: '',
                    });
                    setPasswordConfirm('')
                    navigate("/")
                }
        } else {
            alert("Passwords Do Not Match")
        }


        const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    const nextStep = () => setCurrentStep(currentStep + 1);
    const prevStep = () => setCurrentStep(currentStep - 1);

    return (
        <div className="signup-container">
            <h1>Join FLX Rewards</h1>
            <Stepper currentStep={currentStep} />
            <StepContent currentStep={currentStep} formData={formData} handleInputChange={handleInputChange} />
            <div className="navigation-buttons">
                {currentStep > 1 && <button onClick={prevStep}>Back</button>}
                {currentStep < 3 && <button onClick={nextStep}>Next</button>}
                {currentStep === 3 && <button>Submit</button>}
            </div>
        </div>
    );
};

const Stepper = ({ currentStep }) => {
    return (
        <div className="stepper">
            <div className={`dot ${currentStep === 1 ? 'filled' : ''}`}></div>
            <div className={`dot ${currentStep === 2 ? 'filled' : ''}`}></div>
            <div className={`dot ${currentStep === 3 ? 'filled' : ''}`}></div>
            <label>Join</label>
            <label>Verify</label>
            <label>Done</label>
        </div>
    );
};

const StepContent = ({ currentStep, formData, handleInputChange }) => {
    switch (currentStep) {
        case 1:
            return (
                <>
                <form onSubmit={handleSubmit} id="create-shoe-form"></form>
                    <h2>Account Details</h2>
                    <div className="col-12 mb-2">
                        <input placeholder="Name..." className="text-center form-control" onChange={handleFormChange} value={formData.name} required type="text" name="name" id="name"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Brand..." className="text-center form-control" onChange={handleFormChange} value={formData.brand} required type="text" name="brand" id="brand"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Category..." className="text-center form-control" onChange={handleFormChange} value={formData.category} required type="text" name="category" id="category"/>
                    </div>
                </>
            );
        case 2:
            return (
                <>
                    <h2>Account Details</h2>
                    <div className="col-12 mb-2">
                        <input placeholder="Price..." className="text-center form-control" onChange={handleFormChange} value={formData.price} type="number" step="0.01" name="price" id="price"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Size..." className="text-center form-control" onChange={handleFormChange} value={formData.size} type="number" step="0.01" name="size" id="size"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Color..." className="text-center form-control" onChange={handleFormChange} value={formData.color} required type="text" name="color" id="username"/>
                    </div>
                </>
            );
        case 3:
            return (
                <>
                    <div className="col-12 mb-2">
                        <input placeholder="Sku..." className="text-center form-control" onChange={handleFormChange} value={formData.sku} required type="number" name="sku" id="sku"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Store..." className="text-center form-control" onChange={handleFormChange} value={formData.bin} required type="text" name="bin" id="bin"/>
                    </div>
                </>
            )
        default:
            return null;
    }
};
    }
export default ShoeForm1;
