import {useState, useEffect} from 'react';
import "./Shoes.css"


function ShoeDetail() {
    const [shoe, setShoe] = useState([]);

    async function loadShoe() {
        const response = await fetch('http://localhost:8080/api/shoe/12/');
        const data = await response.json();
        setShoe(data);
        console.log(data.picture_url)
    }
    useEffect(() => {
        loadShoe();
    }, []);



    // async function handleOnDelete(e) {
    //     const pk = e.target.id
    //     const request = await fetch (`http://localhost:8080/api/shoe/${pk}/`, {
    //         method: "DELETE"
    //     });
    //     const resp = await request.json()
    //     if (resp.deleted){
    //         loadShoe()
    //     } else {
    //         alert("Not Deleted!")
    //     }
    // }

return (
    <div className="container1">
        <div className="card cardContainer" key={shoe.id}>
                <div className="nameContainer">
                    <h1 className="name">{shoe.name}</h1>
                </div>
                <div className="col gap-3 nameContainer">
                <p>{shoe.gender}'s</p>
                </div>
                <div>
                <img src={shoe.picture_url} className="imgCard card-img-top"></img>
                </div>
            <div className="card-body">
                <table className="table">
                <thead className="tableHead">
                    <tr className="headTR">
                        <th scope="col">Brand</th>
                        <th scope="col">Category</th>
                        <th scope="col">Price</th>
                        <th scope="col">Size</th>
                        <th scope="col">Color</th>
                        <th scope="col">Sku</th>
                    </tr>
                </thead>
                <tbody className='tableBody'>
                    <tr className="tableHead">
                        <td>{shoe.brand}</td>
                        <td>{shoe.category}</td>
                        <td>${shoe.price}</td>
                        <td>{shoe.size}</td>
                        <td>{shoe.color}</td>
                        <td>{shoe.sku}</td>
                    </tr>
                </tbody>
                </table>
            </div>
        </div>
    </div>
);
}
export default ShoeDetail;
