import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {  faX } from '@fortawesome/free-solid-svg-icons';
import { faStar as farFaStar } from '@fortawesome/free-regular-svg-icons';
import { faStar as fasFaStar } from '@fortawesome/free-solid-svg-icons';
import "./ShoePage.css"


function StarRating({ shoeID }) {
    console.log(shoeID);
    const [hoveredStar, setHoveredStar] = useState(null);
    const [showModel, setShowModel] = useState(false);
    const [selectedStar, setSelectedStar] = useState(null);
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });


    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = `http://localhost:8080/api/ratings/${shoeID}/`;
        const fetchConfig = {
            method: "POST",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
            },
            }
        try {
            const response = await fetch(accountUrl, fetchConfig);

            if (response.ok) {
                setFormData({
                    username: '',
                    password: '',
                });
                setShowModel(false)
            } else {
                console.error('Login failed');
            }
        } catch (error) {
            console.error('Error during Login:', error);
        }
    }


    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }


    const handleStarClick = (starNumber) => {
        setSelectedStar(starNumber);
        setShowModel(true);
        console.log(showModel)
    };


    return (
        <div className="products_star">
            {[1, 2, 3, 4, 5].map((starNumber) => (
                <FontAwesomeIcon
                    key={starNumber}
                    className="starIcon"
                    icon={(starNumber <= (hoveredStar || selectedStar)) ? fasFaStar : farFaStar}
                    onMouseEnter={() => setHoveredStar(starNumber)}
                    onMouseLeave={() => setHoveredStar(null)}
                    onClick={() => handleStarClick(starNumber)}
                    color="transparent"
                    style={{ color: (starNumber <= (hoveredStar || selectedStar)) ? 'gold' : 'grey'}}
                />
            ))}
                {showModel && <div className="overlay"></div>}
                <div className={showModel ? "ratingModel show" : "ratingModel"}>
                    <div className='topSection'>
                        <div className='modelWindowXAndRate'>
                            <div className='rateStarsDiv'>
                                <h2 className='rateStars'>Rate {selectedStar} stars </h2>
                            </div>
                            <div className='exitButtonDiv'>
                                <button onClick={() => setShowModel(false)} className='exitButton'><FontAwesomeIcon size="1x"icon={faX} /></button>
                            </div>
                        </div>
                    </div>
                    <input placeholder="Password..." className="text-center form-control" onChange={handleFormChange} value={formData.password} required type="text" name="password" id="password"/>
                    <div className='ratingContent'>
                        <div className='ratingDescDiv'>
                            <textarea className='ratingDesc' placeholder="Describe your rating..."></textarea>
                        </div>
                        <div className='submitRatingDiv'>
                            <button className="submitRating" onClick={handleSubmit}>Submit</button>
                        </div>
                    </div>

                </div>
        </div>
    );
}

export default StarRating;
