import "./ShoePage.css"
import {useState, useEffect} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faStar, faShare, faHeart } from '@fortawesome/free-solid-svg-icons'
function ShoePage(){

    const [shoes, setShoes] = useState([]);

    async function loadShoes() {
        const response = await fetch('http://localhost:8080/api/shoes/');
        const data = await response.json();
        setShoes(data.shoes);
    }
    useEffect(() => {
        loadShoes();
    }, []);

    return (
        <>
        <div className="products">
            <div>
            <h1>Catalog</h1>
            </div>
            <div className="box gap-4">
                {shoes.map(shoe => {
            return (
                <div className="card">
                        <div className="small_card">
                            <FontAwesomeIcon  className="icon" icon={faHeart} />
                            <FontAwesomeIcon  className="icon" icon={faShare} />
                        </div>
                        <div className="image">
                            <img src={shoe.picture_url}/>
                        </div>
                    <div className="productsText">
                        <h2>{shoe.brand}</h2>
                        <div>
                        <p className="description">
                            lorema sofns aasna fawos gensoa geni
                        </p>
                        </div>
                        <div>
                        <h3>$100.99</h3>
                        </div>
                        <div className="products_star">
                            <FontAwesomeIcon  className="starIcon" icon={faStar} />
                            <FontAwesomeIcon  className="starIcon" icon={faStar} />
                            <FontAwesomeIcon  className="starIcon" icon={faStar} />
                            <FontAwesomeIcon  className="starIcon" icon={faStar} />
                            <FontAwesomeIcon  className="starIcon" icon={faStar} />
                        </div>
                        <a href="#" className="addButton">Add To Account </a>
                    </div>
                </div>
                );
        })}
            </div>
        </div>

        </>
    )
}
export default ShoePage;
