import {useState, useEffect} from 'react';

function ShoeList() {
    const [shoes, setShoes] = useState([]);
    const [sortDirection, setSortDirection] = useState("up");

    async function loadShoes() {
    const response = await fetch('http://localhost:8080/api/bin/1/shoes/');
    const data = await response.json();
    setShoes(data.shoes);
    }
    useEffect(() => {
        loadShoes();
    }, []);


    const sortedList = shoes.sort((conferenceA, conferenceB) => {
        if (sortDirection === "up"){
            return (conferenceA.id - conferenceB.id)
        } else {
            return (conferenceB.id - conferenceA.id)
        }
    })

return (
<table className="table table-striped">
    <thead>
    <tr>
        <th>IDs</th>
        <th>Names</th>
        <th>Brands</th>
        <th>Categories</th>
        <th>Prices</th>
        <th>Colors</th>
        <th>Bins</th>
    </tr>
    </thead>
    <tbody>
    {sortedList.map(shoe => {
        return (
        <tr key={shoe.id}>
            <td>{ shoe.id }</td>
            <td>{ shoe.name }</td>
            <td>{ shoe.brand }</td>
            <td>{ shoe.category }</td>
            <td>{ shoe.price }</td>
            <td>{ shoe.color }</td>
            <td>{ shoe.bin.id }</td>
        </tr>
        );
    })}
    </tbody>
</table>
);
}
export default ShoeList;
