import "./ShoePage.css"
import {useState, useEffect} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faStar, faShare, faHeart } from '@fortawesome/free-solid-svg-icons'
import { Link } from "react-router-dom"
import StarRating from "./Stars"

function ShoePage(){

    const [shoes, setShoes] = useState([]);

    async function loadShoes() {
        const response = await fetch('http://localhost:8080/api/shoes/');
        const data = await response.json();
        setShoes(data.shoes);
    }
    useEffect(() => {
        loadShoes();
    }, []);

    return (
        <>
        <div className="products">
            <div className="h1Container">
            <h1>Catalog</h1>
            </div>
            <div  className="col shoePageColumns">
            <div className="filterSideWindowDiv">
                {shoes.map(shoe => {
                    return (
                <div className="col-6 filterSideWindow">
                    <ol>
                        <li>
                        <select value={bin} onChange={handleBinChange} required name="bin" id="bin" className="form-select">
                            <option className="text-center">Choose a brand</option>
                            {shoes.map(shoe => {
                                return (
                                <option className="text-center" key={bin.href} value={bin.href} >
                                {bin.closet_name}
                                </option>
                                );
                            })}
                        </select>
                        </li>
                        <li>
                        </li>
                    </ol>
                </div>
                    )
                })}
            </div>
            <div className="box col-6 gap-4">
                {shoes.map(shoe => {
            return (
                <div key={shoe.id} className="card">
                    <div className="hoverItems">
                        <div className="small_card">
                            <FontAwesomeIcon  className="iconHeart" icon={faHeart} />
                            <FontAwesomeIcon  className="iconShare" icon={faShare} />
                        </div>
                        <div className="image">
                            <img src={shoe.picture_url}/>
                        </div>
                    <div className="productsText">
                        <div className="shoeNameContainer">
                        <h2><Link className="shoeName" to={`/shoes/${shoe.id}`}>{ shoe.name }</Link></h2>
                        </div>
                        </div>
                        <div>
                            <hr className="p-0 m-0"></hr>
                        <p className="descriptionList">
                            {shoe.description}
                        </p>
                        </div>
                        <div className="shoePrice">
                        <h3>${shoe.price}</h3>
                        </div>
                        <div className="products_star">
                            <StarRating/>
                        </div>
                        <div className="addBtnContainer">
                        <a href="#" className="addButton">Add To Account </a>
                        </div>
                    </div>
                </div>
                );
        })}
            </div>
            </div>
        </div>

        </>
    )
}
export default ShoePage;
