import React, { useState, useEffect } from 'react';

function AccountLogIn() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
    });


    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/account/login/';
        const fetchConfig = {
            method: "POST",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': csrfToken,
            },
            credentials: 'include',
            }
        try {
            const response = await fetch(accountUrl, fetchConfig);

            if (response.ok) {
                login();
                setFormData({
                    username: '',
                    password: '',
                });
                navigate("/")
            } else {
                console.error('Login failed');
            }
        } catch (error) {
            console.error('Error during Login:', error);
        }
    }


    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    return (
        <div className="row mainContainer">
            <div className="col-5 mt-4 form1">
                <div className="shadow p-5 mt-1">
                    <div className="col-12 mb-3 mt-0 pt-0 formHeaderContainer" >
                    <h1 className='text-center mb-3 formHeader'>Sign In</h1>
                    </div>
                    <form onSubmit={handleSubmit} id="sign-in-form">
                        <div className="col-12 mb-2">
                            <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                        </div>
                        <div className="col-12 mt-4 mb-2">
                            <input placeholder="Password..." className="text-center form-control" onChange={handleFormChange} value={formData.password} required type="text" name="password" id="password"/>
                        </div>
                        <button className="btn btn-primary mt-3 col-12">Create</button>
                    </form>
                </div>
            </div>
        </div>
    );
}
export default AccountLogIn;
