import {useState, useEffect} from 'react';
import { BrandsArrowToggleButton, StoresArrowToggleButton, PricesArrowToggleButton, ColorsArrowToggleButton, CategoriesArrowToggleButton, NamesArrowToggleButton } from './filter';

import "./ShoeList.css"
function ShoeList() {
    const [shoes, setShoes] = useState([]);
    const [bins, setBins] = useState([]);
    const [bin, setBin] = useState("");
    // const [filteredList, setFilteredList] = useState([]);
    const [nameIsUp, setNameIsUp] = useState(false);
    const [brandIsUp, setBrandIsUp] = useState(false);
    const [priceIsUp, setPriceIsUp] = useState(false);
    console.log(brandIsUp, "1")

    async function loadShoes() {
        const response = await fetch('http://localhost:8080/api/shoes/');
        const data = await response.json();
        setShoes(data.shoes);
    }


    async function loadBins(){
        const response = await fetch ("http://localhost:8100/api/bins/");
        const data = await response.json();
        setBins(data.bins)
    }
    useEffect(() => {
        loadShoes();
        loadBins();
    }, []);


    async function filterShoes() {
        if (bin !== ""){
            const pk = bin
            const response = await fetch(`http://localhost:8080/api/bin/${pk}/shoes/`);
            const data = await response.json();
            setShoes(data.shoes);
        }
    }


    function handleBinChange(e){
            setBin(e.target.value)
    }
    useEffect(() => {
        filterShoes();
    }, [bin]);


    function handleAllShoesClick(){
        loadShoes();
    }


    async function handleOnDelete(e) {
        const pk = e.target.id
        const request = await fetch (`http://localhost:8080/api/shoe/${pk}/`, {
            method: "DELETE"
        });
        const resp = await request.json()
        if (resp.deleted){
            loadShoes()
        } else {
            alert("Not Deleted!")
        }
    }

    function onToggleNameDirection(){

    }

    function onToggleBrandDirection(){

    }

    const sortedList = shoes.sort((shoeA, shoeB) => {
        if (nameIsUp === true) {
            return shoeA.name.localeCompare(shoeB.name);
        } else if (nameIsUp === false){
            return shoeB.name.localeCompare(shoeA.name);
        }  if (brandIsUp === true) {
            return shoeA.brand.localeCompare(shoeB.brand);
        } else if (brandIsUp === false){
            return shoeB.brand.localeCompare(shoeA.brand);
        } if (priceIsUp === true) {
            return shoeA.price - shoeB.price;
        } else if (priceIsUp === false){
            return shoeB.price - shoeA.price;}
            // else if (sortName === "priceUp") {
        //     return shoeA.price - shoeB.price;
        // } else if (sortName === "priceDown"){
        //     return shoeB.price - shoeA.price;
        // }  else if (sortName === "priceUp") {
        //     return shoeA.price - shoeB.price;
        // } else if (sortName === "priceDown"){
        //     return shoeB.price - shoeA.price;
        // }
    });

return (
    <div className="mainDiv">
    <div style={{display: "flex", justifyContent: "center" }}>
    <select style={{width: "20vw"}} value={bin} onChange={handleBinChange} required name="bin" id="bin">
        <option style={{textAlign: "center"}} value="up">Choose a store</option>
        {bins.map(bin => {
            return (
            <option key={bin.id} value={bin.id} >
            {bin.closet_name}
            </option>
            );
        })}
    </select>
    </div>
<table className="table table-striped">
    <thead>
    <tr>
        <th>Names <NamesArrowToggleButton onToggleNameDirection={setNameIsUp}/></th>
        <th>Brands <BrandsArrowToggleButton onToggleBrandDirection={setBrandIsUp}/></th>
        <th>Categories<CategoriesArrowToggleButton/></th>
        <th>Prices<PricesArrowToggleButton onTogglePriceDirection={setPriceIsUp}/></th>
        <th>Colors<ColorsArrowToggleButton/></th>
        <th>Store<StoresArrowToggleButton/></th>
        <th>Remove</th>
    </tr>
    </thead>
    <tbody>
    {sortedList.map(shoe => {
        return (
        <tr key={shoe.id}>
            <td>{ shoe.name }</td>
            <td>{ shoe.brand }</td>
            <td>{ shoe.category }</td>
            <td> ${ shoe.price }</td>
            <td>{ shoe.color }</td>
            <td>{ shoe.bin.name }</td>
            <td><button onClick={handleOnDelete} id={shoe.id}className="btn btn-danger btn-sm">Delete</button></td>
        </tr>
        );
    })}
    </tbody>
</table>
<div style={{display: "flex", justifyContent: "center"}}>
<button style={{width: "15vw", fontSize: "1em", overflow: "hidden"}}onClick={handleAllShoesClick}>Refresh</button>
</div>
</div>
);
}
export default ShoeList;
