import {useState, useEffect} from 'react';
import { BrandsArrowToggleButton, CategoriesArrowToggleButton, NamesArrowToggleButton } from './filter';

import "./ShoeList.css"
function ShoeList() {
    const [shoes, setShoes] = useState([]);
    const [bins, setBins] = useState([]);
    const [bin, setBin] = useState("");
    const [sortName, setSortName] = useState("nameUp");
    const [nameIsUp, setNameIsUp] = useState(false);
    const [brandIsUp, setBrandIsUp] = useState(false);


    async function loadShoes() {
        const response = await fetch('http://localhost:8080/api/shoes/');
        const data = await response.json();
        setShoes(data.shoes);
    }


    async function loadBins(){
        const response = await fetch ("http://localhost:8100/api/bins/");
        const data = await response.json();
        setBins(data.bins)
    }
    useEffect(() => {
        loadShoes();
        loadBins();
    }, []);


    async function filterShoes() {
        if (bin !== ""){
            const pk = bin
            const response = await fetch(`http://localhost:8080/api/bin/${pk}/shoes/`);
            const data = await response.json();
            setShoes(data.shoes);
        }
    }


    function handleBinChange(e){
            setBin(e.target.value)
    }
    useEffect(() => {
        filterShoes();
    }, [bin]);


    function handleAllShoesClick(){
        loadShoes();
    }


    async function handleOnDelete(e) {
        const pk = e.target.id
        const request = await fetch (`http://localhost:8080/api/shoe/${pk}/`, {
            method: "DELETE"
        });
        const resp = await request.json()
        if (resp.deleted){
            loadShoes()
        } else {
            alert("Not Deleted!")
        }
    }


    function handleSortChange(e){
        setSortName(e.target.value)
    }

    const sortedList = shoes.sort((shoeA, shoeB) => {
        if (nameIsUp === true) {
            return shoeA.name.localeCompare(shoeB.name);
        } else if (nameIsUp === false){
            return shoeB.name.localeCompare(shoeA.name);
        } else if (brandisUp === true) {
            return shoeA.price - shoeB.price;
        } else if (BrandisUp === false){
            return shoeB.price - shoeA.price;
        }  else if (sortName === "priceUp") {
            return shoeA.price - shoeB.price;
        } else if (sortName === "priceDown"){
            return shoeB.price - shoeA.price;
        }  else if (sortName === "priceUp") {
            return shoeA.price - shoeB.price;
        } else if (sortName === "priceDown"){
            return shoeB.price - shoeA.price;
        }  else if (sortName === "priceUp") {
            return shoeA.price - shoeB.price;
        } else if (sortName === "priceDown"){
            return shoeB.price - shoeA.price;
        }
    });

return (
    <div className="mainDiv">
    <select value={bin} onChange={handleBinChange} required name="bin" id="bin">
        <option value="up">Choose a store</option>
        {bins.map(bin => {
            return (
            <option key={bin.id} value={bin.id} >
            {bin.closet_name}
            </option>
            );
        })}
    </select>
    <select onChange={handleSortChange} value="default">
        <option value="default" disabled>
            Name Filter
        </option>
        <option value="nameUp">Up</option>
        <option value="nameDown">Down</option>
    </select>
    <select onChange={handleSortChange} value="default">
        <option value="default" disabled>
            Price Filter
        </option>
        <option value="priceUp">Up</option>
        <option value="priceDown">Down</option>
    </select>
<table className="table table-striped">
    <thead>
    <tr>
        <th>Names <NamesArrowToggleButton onToggleDirection={setNameIsUp}/></th>
        <th>Brands<BrandsArrowToggleButton onToggleDirection={setBrandIsUp}/></th>
        <th>Categories</th>
        <th>Prices</th>
        <th>Colors</th>
        <th>Store</th>
        <th>Remove</th>
    </tr>
    </thead>
    <tbody>
    {sortedList.map(shoe => {
        return (
        <tr key={shoe.id}>
            <td>{ shoe.name }</td>
            <td>{ shoe.brand }</td>
            <td>{ shoe.category }</td>
            <td> ${ shoe.price }</td>
            <td>{ shoe.color }</td>
            <td>{ shoe.bin.name }</td>
            <td><button onClick={handleOnDelete} id={shoe.id}className="btn btn-danger btn-sm">Delete</button></td>
        </tr>
        );
    })}
    </tbody>
</table>
<div style={{display: "flex", justifyContent: "center"}}>
<button style={{width: "10vw"}}onClick={handleAllShoesClick}>Refresh</button>
</div>
</div>
);
}
export default ShoeList;
