import React, { useState } from 'react';
import './ShoeForm1.css';

function AccountForm() {
    const [passwordConfirm, setPasswordConfirm] = useState("");
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        name: "",
        brand: "",
        category: "",
        price: "",
        size: "",
        color: "",
        sku: "",
        bin: ""
    });

    const handleSubmit = async (event) => {
        event.preventDefault();
        // ... rest of your handleSubmit function
    };

    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    const nextStep = () => setCurrentStep(currentStep + 1);
    const prevStep = () => setCurrentStep(currentStep - 1);

    const renderFormContent = () => {
        switch (currentStep) {
            case 1:
                return (
                    <>
                        <h2>Account Details</h2>
                        <div className="col-12 mb-2">
                            <input placeholder="Name..." className="text-center form-control" onChange={handleFormChange} value={formData.name} required type="text" name="name" id="name" />
                        </div>
                        // ... rest of your case 1 inputs
                    </>
                );
            // ... your case 2 and 3 content
            default:
                return null;
        }
    };

    return (
        <div className="signup-container">
            <h1>Join FLX Rewards</h1>
            <Stepper currentStep={currentStep} />
            <form onSubmit={handleSubmit} id="create-service-form">
                {renderFormContent()}
                <div className="navigation-buttons">
                    {currentStep > 1 && <button type="button" onClick={prevStep}>Back</button>}
                    {currentStep < 3 && <button type="button" onClick={nextStep}>Next</button>}
                    {currentStep === 3 && <button type="submit">Submit</button>}
                </div>
            </form>
        </div>
    );
}

const Stepper = ({ currentStep }) => {
    return (
        <div className="stepper">
            <div className={`dot ${currentStep === 1 ? 'filled' : ''}`}></div>
            // ... rest of your stepper content
        </div>
    );
};

export default AccountForm;
