import {useState} from 'react';

export function NamesArrowToggleButton(props) {
    const [nameIsUp, setNameIsUp] = useState(false);

    const toggleArrowDirection = () => {
        setNameIsUp(!nameIsUp);
        props.onToggleDirection(!nameIsUp);
    };
    return (
        <button className="btn btn-sm" onClick={toggleArrowDirection}>
            {nameIsUp ? (
            <span>&#9650;</span>
            ) : (
            <span>&#9660;</span>
            )}
        </button>
    );
    }


export function BrandsArrowToggleButton(props) {
    const [brandIsUp, setBrandIsUp] = useState(false);

    const toggleArrowDirection = () => {
        setBrandIsUp(!brandIsUp);
        props.onToggleDirection(!brandIsUp);
    };
    return (
        <button className="btn btn-sm" onClick={toggleArrowDirection}>
            {brandIsUp ? (
            <span>&#9650;</span>
            ) : (
            <span>&#9660;</span>
            )}
        </button>
    );
    }


export function CategoriesArrowToggleButton() {
    const [isUp, setIsUp] = useState(true);

    const toggleArrowDirection = () => {
        setIsUp(!isUp);
    };
    return (
        <button className="btn btn-sm" onClick={toggleArrowDirection}>
            {isUp ? (
            <span>&#9650;</span>
            ) : (
            <span>&#9660;</span>
            )}
        </button>
    );
    }

export function ColorsArrowToggleButton() {
    const [isUp, setIsUp] = useState(true);

    const toggleArrowDirection = () => {
        setIsUp(!isUp);
    };
    return (
        <button className="btn btn-sm" onClick={toggleArrowDirection}>
            {isUp ? (
            <span>&#9650;</span>
            ) : (
            <span>&#9660;</span>
            )}
        </button>
    );
    }

export function StoreCategoriesArrowToggleButton() {
    const [isUp, setIsUp] = useState(true);

    const toggleArrowDirection = () => {
        setIsUp(!isUp);
    };
    return (
        <button className="btn btn-sm" onClick={toggleArrowDirection}>
            {isUp ? (
            <span>&#9650;</span>
            ) : (
            <span>&#9660;</span>
            )}
        </button>
    );
    }
