import React, { useState } from 'react';
import './ShoeForm1.css';  // Assuming you'll add some CSS


    function AccountForm() {
        const [passwordConfirm, setPasswordConfirm] = useState("")
        const [currentStep, setCurrentStep] = useState(1);
        const [formData, setFormData] = useState({
                name: "",
                brand: "",
                categoryr: "",
                price: "",
                size: "",
                color: "",
                sku: "",
                bin: ""
        });


    const handleSubmit = async (event) => {
        event.preventDefault();
        const accountUrl = 'http://localhost:8070/api/accounts/';
        const fetchConfig = {
            method: "post",
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json',
                'X-CSRFToken': csrfToken,
            },
            credentials: 'include',
            }

        if (formData.password === passwordConfirm){
            const response = await fetch(accountUrl, fetchConfig);
                if (response.ok) {
                    login()
                    setFormData({
                        username: '',
                        first_name: '',
                        last_name: '',
                        email: '',
                        password: '',
                    });
                    setPasswordConfirm('')
                    navigate("/")
                }
        } else {
            alert("Passwords Do Not Match")
        }


        const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    const nextStep = () => setCurrentStep(currentStep + 1);
    const prevStep = () => setCurrentStep(currentStep - 1);

    return (
        <div className="signup-container">
            <h1>Join FLX Rewards</h1>
            <Stepper currentStep={currentStep} />
            <StepContent currentStep={currentStep} formData={formData} handleInputChange={handleInputChange} />
            <div className="navigation-buttons">
                {currentStep > 1 && <button onClick={prevStep}>Back</button>}
                {currentStep < 3 && <button onClick={nextStep}>Next</button>}
                {currentStep === 3 && <button>Submit</button>}
            </div>
        </div>
    );
};

const Stepper = ({ currentStep }) => {
    return (
        <div className="stepper">
            <div className={`dot ${currentStep === 1 ? 'filled' : ''}`}></div>
            <div className={`dot ${currentStep === 2 ? 'filled' : ''}`}></div>
            <div className={`dot ${currentStep === 3 ? 'filled' : ''}`}></div>
            <label>Join</label>
            <label>Verify</label>
            <label>Done</label>
        </div>
    );
};

const StepContent = ({ currentStep, formData, handleInputChange }) => {
    switch (currentStep) {
        case 1:
            return (
                <>
                    <h2>Account Details</h2>
                    <div className="col-12 mb-2">
                        <input placeholder="Name..." className="text-center form-control" onChange={handleFormChange} value={formData.name} required type="text" name="name" id="name"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="brand..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                </>
            );
        case 2:
            return (
                <>
                    <h2>Account Details</h2>
                    <div className="col-12 mb-2">
                        <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                </>
            );
        case 3:
            return (
                <>
                    <div className="col-12 mb-2">
                        <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                    <div className="col-12 mb-2">
                        <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                    </div>
                </>
            )
        default:
            return null;
    }
};



    return (
            <div className="mt-4 p-4 mainDivSignIn" >
                <div className="shadow p-5 mt-2 form2">
                    <form onSubmit={handleSubmit} id="create-account-form">
                        <div className="mb-3 mt-0 pt-0 formHeaderContainer" >
                            <h1 className='text-center mb-3 formHeader'>Create an Account With Us</h1>
                        </div>
                        <div className="row" >
                        <div className="col-12 mb-2">
                            <input placeholder="Username..." className="text-center form-control" onChange={handleFormChange} value={formData.username} required type="text" name="username" id="username"/>
                        </div>
                        <div className="col-6 mt-4 mb-2 inputField1">
                            <input placeholder="First Name..." className="text-center form-control" onChange={handleFormChange} value={formData.first_name} required type="text" name="first_name" id="first_name"/>
                        </div>
                        <div className="col-6 mt-4 mb-2 inputField2">
                            <input placeholder="Last Name..." className="text-center form-control" onChange={handleFormChange} value={formData.last_name} required type="text" name="last_name" id="last_name"/>
                        </div>
                        <div className="col-12 mt-4 mb-2">
                            <input placeholder="Email..." className="text-center form-control" onChange={handleFormChange} value={formData.email} required type="text" name="email" id="email"/>
                        </div>
                        <div className="col-6 mt-4 inputField1">
                            <input placeholder="Password..." className="text-center form-control" onChange={handleFormChange} value={formData.password} required type="text" name="password" id="password"/>
                        </div>
                        <div className="col-6 mt-4 mb-2 inputField2">
                            <input placeholder="Password Confirmation..." className="text-center form-control" onChange={handlePasswordConfirmChange} value={passwordConfirm} required type="text" name="passwordConfirm" id="passwordConfirm"/>
                        </div>
                        <div className="col">
                        <button className="btn col-12 btn-primary mt-4 createButton">Create</button>
                        </div>
                        </div>
                    </form>
                </div>
            </div>
    );
}
export default AccountForm;
