import React, { createContext, useState, useContext } from 'react';
import "./AccountForm.css"
import { BrandsArrowToggleButton, StoresArrowToggleButton, PricesArrowToggleButton, ColorsArrowToggleButton, CategoriesArrowToggleButton, NamesArrowToggleButton } from './accounts';
const ModalContext = createContext();

export function useModal() {
    return useContext(ModalContext);
}

function SignInModal({ isModalOpen, closeModal }) {
    console.log(isModalOpen)

    return (
        <div className={isModalOpen ? "ratingModel show" : "ratingModel"}>
            <button onClick={closeModal}>Close</button>
            <AccountLogIn closeModal={closeModal} />
        </div>
    );
}

export function ModalProvider({ children }) {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => setIsModalOpen(true);
    const closeModal = () => setIsModalOpen(false);

    return (
        <ModalContext.Provider value={{ isModalOpen, openModal, closeModal }}>
            {children}
            <SignInModal isModalOpen={isModalOpen} closeModal={closeModal} />
        </ModalContext.Provider>
    );
}
