import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';

function StarRating() {
    const [hoveredStar, setHoveredStar] = useState(4);
    const [showModal, setShowModal] = useState(false);
    const [selectedStar, setSelectedStar] = useState(null);

    const [csrfToken, setCsrfToken] = useState('');
    const [formData, setFormData] = useState({
        rating: '',
        rating_description: '',
    });
    const navigate = useNavigate();


    const handleStarClick = (rating) => {
        setSelectedStar(rating);
        setShowModal(true);
    };


    const handleSubmit = async (event) => {
        // event.preventDefault();
        // const accountUrl = 'http://localhost:8070/api/account/login/';
        // const fetchConfig = {
        //     method: "POST",
        //     body: JSON.stringify(formData),
        //     headers: {
        //         'Content-Type': 'application/json',
        //         'X-CSRFToken': csrfToken,
        //     },
        //     credentials: 'include',
        //     }
        // try {
        //     const response = await fetch(accountUrl, fetchConfig);

        //     if (response.ok) {
        //         setFormData({
        //             rating: '',
        //             rating_description: ''
        //         });
        //         navigate("/")
        //     } else {
        //         console.error('Login failed');
        //     }
        // } catch (error) {
        //     console.error('Error during Login:', error);
        // }
    }


    // const handleFormChange = (e) => {
    //     const value = e.target.value;
    //     const inputName = e.target.name;
    //     setFormData({
    //         ...formData,
    //         [inputName]: value
    //     });
    // }
    const getColor = (starNumber) => {
        if (starNumber <= hoveredStar) return 'gold';
        if (starNumber <= selectedStar) return 'gold';
        return 'grey';
    };

    return (
        <div className="products_star">
            {[1, 2, 3, 4, 5].map((starNumber) => (
                <FontAwesomeIcon
                    key={starNumber}
                    className="starIcon"
                    icon={faStar}
                    onMouseEnter={() => setHoveredStar(starNumber)}
                    onMouseLeave={() => setHoveredStar(starNumber)}
                    onClick={() => setSelectedStar(starNumber)}
                    style={{color: getColor(starNumber)}}
                />
            ))}
            {showModal && (
                <div className="modal">
                    <h2>Rate {selectedStar} stars</h2>
                    <textarea placeholder="Describe your rating..."></textarea>
                    <button onClick={() => setShowModal(false)}>Submit</button>
                </div>
            )}
        </div>
    );
}

export default StarRating;
