import "./ShoePage.css"
import {useState, useEffect} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faStar, faShare, faHeart } from '@fortawesome/free-solid-svg-icons'
import { Link } from "react-router-dom"
import StarRating from "./Stars"
import { FilterBrands, FilterCategory, FilterPrice } from "./ShoePageFilter";
import img1 from '../images/img1.png';
import img2 from '../images/img2.png';
import img3 from '../images/img3.png';
import img4 from '../images/img4.png';
import img5 from '../images/img5.png';
import img6 from '../images/img6.png';
import img7 from '../images/img7.png';
import img8 from '../images/img8.png';
import img9 from '../images/img9.png';
import img10 from '../images/img10.png';


const data = {"images": [{"image": img1}, {"image": img2}, {"image": img3},
{"image": img4}, {"image": img5}, {"image": img6}, {"image": img7}, {"image": img8}, {"image": img9}, {"image": img10}]}


function ShoePage(){

    const [shoes, setShoes] = useState([]);
    const [shoes1, setShoes1] = useState([]);
    const [filteredShoes, setFilteredShoes] = useState(shoes);
    setShoes1(data.images)

    async function loadShoes() {
        const response = await fetch('http://localhost:8080/api/shoes/');
        const data = await response.json();
        setShoes(data.shoes);
    }
    useEffect(() => {
        loadShoes();
    }, []);


    const handleBrandFilter = (brand) => {
        if (brand === "Brand") {
            setFilteredShoes(shoes);
        } else {
            setFilteredShoes(shoes.filter(shoe => shoe.brand === brand));
        }
    };
    const handleCategoryFilter = (category) => {
        if (category === "Category") {
            setFilteredShoes(shoes);
        } else {
            setFilteredShoes(shoes.filter(shoe => shoe.category === category));
        }
    };

    const handlePriceFilter = (selectedLabel) => {
        if (selectedLabel === "Price Range") {
            setFilteredShoes(shoes);
        } else {
            const range = priceRanges.find(r => r.label === selectedLabel);
            if (range) {
                setFilteredShoes(shoes.filter(shoe => shoe.price >= range.min && shoe.price <= range.max));
            }
        }
    };



    const priceRanges = [
        { label: '$1 - $50', min: 1, max: 50 },
        { label: '$50 - $100', min: 50, max: 100 },
        { label: '$100 - $150', min: 100, max: 150 },
        { label: '$150 - $200', min: 150, max: 200 }
    ];

    return (
        <>
        <div className="products">
            <div className="h1Container">
            <h1>Catalog</h1>
            </div>
            <div  className="col shoePageColumns">
            <div className="filterSideWindowDiv">
                <div className="col-6 filterSideWindow">
                    <ul className="filterList">
                    <li className="filterHeaderDiv">
                        <h2 className="filterHeader">
                            Filters
                        </h2>
                    </li>
                    <li className="col-12 filterListItem">
                        <FilterBrands shoes={shoes} onBrandSelected={handleBrandFilter} />
                    </li>
                    <li className="col-12 filterListItem">
                        <FilterCategory shoes={shoes} onCategorySelected={handleCategoryFilter} />
                    </li>
                    <li className="col-12 filterListItem">
                        <FilterPrice shoes={shoes} onPriceSelected={handlePriceFilter} />
                    </li>
                    </ul>
                </div>
            </div>
            <div className="box col-6 gap-4">
                {shoes.map(shoe => {
            return (
                <div key={shoe.id} className="card">
                    <div className="hoverItems">
                        <div className="small_card">
                            <FontAwesomeIcon  className="iconHeart" icon={faHeart} />
                            <FontAwesomeIcon  className="iconShare" icon={faShare} />
                        </div>
                        <div className="image">
                            {shoes1.map((image, index) => {
                                return (
                                    <div key={index}>
                                        <img src={image}/>
                                    </div>
                            )})}
                        </div>
                    <div className="productsText">

                        <div className="shoeNameContainer">
                        <h2><Link className="shoeName" to={`/shoes/${shoe.id}`}>{ shoe.name }</Link></h2>
                        </div>
                        </div>
                        <div className="shoePrice">
                        <h3>${shoe.price}</h3>
                        </div>
                        <div className="products_star">
                            <StarRating/>
                        </div>
                        <div className="addBtnContainer">
                        <a href="#" className="addButton">Add To Account </a>
                        </div>
                    </div>
                </div>
                );
        })}
            </div>
            </div>
        </div>

        </>
    )
}
export default ShoePage;
