import {useState} from 'react';


export function FilterBrands({ shoes, onBrandSelected }) {
    const [selectedBrand, setSelectedBrand] = useState("");

    const handleBrandChange = (e) => {
        setSelectedBrand(e.target.value);
        onBrandSelected(e.target.value);
    };

    let uniqueBrands = new Set();
    shoes.forEach(shoe => uniqueBrands.add(shoe.brand));
    let brandsArray = [...uniqueBrands];

    return (
        <div>
            <select value={selectedBrand} onChange={handleBrandChange} required name="brand" id="brand" className="form-select">
                <option className="text-center">Choose a brand</option>
                {brandsArray.map(brand => (
                    <option className="text-center" key={brand} value={brand}>
                        {brand}
                    </option>
                ))}
            </select>
        </div>
    );
}


export function FilterCategory({ shoes, onCategorySelected }) {
    const [selectedCategory, setSelectedCategory] = useState("");

    const handleCategoryChange = (e) => {
        setSelectedCategory(e.target.value);
        onCategorySelected(e.target.value);
    };

    let uniqueCategories = new Set();
    shoes.forEach(shoe => uniqueBrands.add(shoe.category));
    let brandsArray = [...uniqueBrands];

    return (
        <div>
            <select value={selectedCategory} onChange={handleBrandChange} required name="brand" id="brand" className="form-select">
                <option className="text-center">Choose a brand</option>
                {brandsArray.map(brand => (
                    <option className="text-center" key={brand} value={brand}>
                        {brand}
                    </option>
                ))}
            </select>
        </div>
    );
}
