import React, { useState } from 'react';
import './SignUpScreen.css';  // Assuming you'll add some CSS

const SignUpScreen = () => {
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        dob: '',
        zipCode: '',
        gender: ''
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const nextStep = () => setCurrentStep(currentStep + 1);
    const prevStep = () => setCurrentStep(currentStep - 1);

    return (
        <div className="signup-container">
            <h1>Join FLX Rewards</h1>
            <Stepper currentStep={currentStep} />
            <StepContent currentStep={currentStep} formData={formData} handleInputChange={handleInputChange} />
            <div className="navigation-buttons">
                {currentStep > 1 && <button onClick={prevStep}>Back</button>}
                {currentStep < 3 && <button onClick={nextStep}>Next</button>}
                {currentStep === 3 && <button>Submit</button>}
            </div>
        </div>
    );
};

const Stepper = ({ currentStep }) => {
    return (
        <div className="stepper">
            <div className={`dot ${currentStep === 1 ? 'filled' : ''}`}></div>
            <div className={`dot ${currentStep === 2 ? 'filled' : ''}`}></div>
            <div className={`dot ${currentStep === 3 ? 'filled' : ''}`}></div>
            <label>Join</label>
            <label>Verify</label>
            <label>Done</label>
        </div>
    );
};

const StepContent = ({ currentStep, formData, handleInputChange }) => {
    switch (currentStep) {
        case 1:
            return (
                <>
                    <h2>Account Details</h2>
                    <input
                        name="firstName"
                        placeholder="First Name"
                        value={formData.firstName}
                        onChange={handleInputChange}
                    />
                    <input
                        name="lastName"
                        placeholder="Last Name"
                        value={formData.lastName}
                        onChange={handleInputChange}
                    />
                </>
            );
        case 2:
            return (
                <>
                    <h2>Account Details</h2>
                    <input
                        name="dob"
                        placeholder="Date of Birth (MM/DD/YYYY)"
                        value={formData.dob}
                        onChange={handleInputChange}
                    />
                    <input
                        name="zipCode"
                        placeholder="U.S. Zip Code"
                        value={formData.zipCode}
                        onChange={handleInputChange}
                    />
                </>
            );
        case 3:
            return <p>Confirm your details and submit!</p>;
        default:
            return null;
    }
};

export default SignUpScreen;
